// ==UserScript==
// @name         Neptun - Watch exam
// @namespace    https://github.com/elekdavid/tampermonkey-scripts
// @source       https://github.com/elekdavid/tampermonkey-scripts/raw/master/neptun_watch_exam.user.js
// @namespace    http://tampermonkey.net/
// @version      0.1
// @description  Watches exams on Neptun and singes on if there is a space.
// @author       David Elek
// @match        https://frame.neptun.bme.hu/hallgatoi/main.aspx*ctrl=0401*
// @grant        GM_getValue
// @grant        GM_setValue
// @grant        GM_deleteValue
// ==/UserScript==

(function() {
    'use strict';

    const waitForRefresh = 15000;

    const watchButtonHtml =
          "<td onmousemove=\"A2.ToolTip(this,'A létszám változásának figyelése és jelentkezés.')\" onmouseout=\"A2.ToolTip(this,'')\">" +
          "  <input type='checkbox' class='watchCheckbox'>" +
          "</td>";

    const watchHeadHtml =
          '<th id="head_figyel" class="header" colname="figyel" align="center" onmousemove="A2.ToolTip(this,\'Automatikus oldalfrissítés 15 másodpercenként\')" onmouseout="A2.ToolTip(this,\'\')">' + 
          '  <div>' +
          '    <span>Auto jelentkezés</span>' +
          '  </div>' +
          '  <input type="checkbox" id="autoRefreshCheckbox">' +
          '  <label for="autoRefreshCheckbox" style="font-size: 8px;"> Auto figyelés </label>' +
          '  <span id="autoRefreshCounter" style="font-size: 8px;"></span>' +
          '</th>';

    var refreshTimeout = {
        timeout: undefined,
        counterInterval: undefined,
        date: undefined
    };

     function beep() {
        var snd = new Audio("data:audio/wav;base64,");  
        snd.loop = true;
        snd.play();
        setTimeout(function(){ snd.pause(); }, 3000);
    }

    function resetRefreshTimeout() {
        if (refreshTimeout.timeout) {
            clearTimeout(refreshTimeout.timeout);
        }

        if (refreshTimeout.interval) {
            clearInterval(refreshTimeout.interval);
        }

        refreshTimeout.timeout = setTimeout(function(){
            location.reload();
        }, waitForRefresh);

        refreshTimeout.date = new Date();
        refreshTimeout.interval = setInterval(function(){
            var dif = new Date().getTime() - refreshTimeout.date.getTime();
            $("#autoRefreshCounter").text("(" + Math.max(0, Math.floor((waitForRefresh - dif) / 1000)) + ")");
        }, 100);
    }

    function clearRefreshTimeout() {
        if (refreshTimeout.timeout) {
            clearTimeout(refreshTimeout.timeout);
        }

        if (refreshTimeout.interval) {
            clearInterval(refreshTimeout.interval);
        }

        $("#autoRefreshCounter").text("");
        refreshTimeout.timeout = undefined;
        refreshTimeout.interval = undefined;
    }

    function haveWatch() {
        var haveWatchBool = false;
        $("#h_exams_gridExamList_bodytable input.watchCheckbox").each(function(){
            if ($(this).prop("checked")) {
                haveWatchBool = true;
                return false;
            }
        });
        return haveWatchBool;
    }

    $("#h_exams_gridExamList_headerrow #head_limit").after(watchHeadHtml);

    $("#h_exams_gridExamList_bodytable > tbody > tr > :nth-child(8)").each(function(){
        $(this).after(watchButtonHtml);

        var trId = $(this).parents("tr").attr('id');

        if (GM_getValue(trId) !== undefined ) {
            var numbers = $(this).html().match(/\d+/g);
            var students = numbers.length < 1 ? 0 : Number(numbers[0]);
            var limit = numbers.length < 2 ? 0 : Number(numbers[1]);

            if (students < limit) {
                GM_deleteValue(trId);
                $(this).next().prop("checked", false);

                // Sign up
                $(this).siblings().last().trigger("click");
                $("#h_exams_gridExamList_contextdiv1 > table > tbody > tr > td").filter(function(){
                    return $(this).prop("onclick") && $(this).prop("onclick").toString().indexOf("signing") !== -1;
                }).trigger("click");

                beep();
            } else {
                $("#" + trId + " input.watchCheckbox").prop("checked", true);
            }
        }
    });

    $("#autoRefreshCheckbox").change(function(){
        if (this.checked){
            resetRefreshTimeout();
            GM_setValue("autoRefreshCheckbox", true);
        } else {
            clearRefreshTimeout();
            GM_deleteValue("autoRefreshCheckbox");
        }
    });

    if (haveWatch()) {
        if (GM_getValue("autoRefreshCheckbox")) {
            $("#autoRefreshCheckbox").prop("checked", true);
            $("#autoRefreshCheckbox").trigger("change");
        }
    } else {
        GM_deleteValue("autoRefreshCheckbox");
    }

    $("#h_exams_gridExamList_bodytable input.watchCheckbox").change(function(){
        var trId = $(this).parents("tr").attr('id');
        if (this.checked){
            GM_setValue(trId, true);
            $("#autoRefreshCheckbox").prop("checked", true);
            $("#autoRefreshCheckbox").trigger("change");
        } else {
             GM_deleteValue(trId);
            if (!haveWatch()) {
                $("#autoRefreshCheckbox").prop("checked", false);
                $("#autoRefreshCheckbox").trigger("change");
            }
        }
    });

})();